ï»¿
$color_h1:tomato;
$color_body: bisque;
$color_nav: dodgerblue;
//deepskyblue,dodgerblue,highlight
$color_hover: deepskyblue;


h1 {
    color: $color_h1;
}
////
* {
    margin: 0;
    padding: 0;
}

body {
    background-color: $color_body;   
}

header {
    width: 100%;

    nav {
        width: 90%;
        max-width: 1000px;
        margin: 20px auto;
        background-color: $color_nav;

        ul {
            overflow: hidden;
            list-style: none;

            li {
                float: left;

                a {
                    color: #fff;
                    padding: 20px;
                    display: block;
                    text-decoration: none;

                    &:hover {
                        background-color:$color_hover;
                    }
                }

                span {
                    margin-right: 10px;
                }
            }
        }
    }
}

.menu_bar {
    display: none;
}

section {
    padding: 20px;
}

@media screen and (max-width:800px ) {
    header {
        nav {
            width: 80%;
            height: 100%;
            left: -100%;
            margin: 0;
            position: fixed;

            ul {
                li {
                    display: block;
                    float: none;
                    border-bottom: 1px solid rgba(255,255,255, .3);
                }
            }
        }
    }

    .menu_bar {
        display: block;
        width: 100%;
        background: #ccc;

        .bt-menu {
            display: block;
            padding: 20px;
            background: #024959;
            color: #fff;
            text-decoration: none;
            font-weight: bold;
            font-size: 25px;
            -webkit-box-sizing: border-box;
            -moz-box-sizing: border-box;
            box-sizing: border-box;
        }

        span {
            float: right;
            font-size: 40px;
        }
    }
}
